// All external libs URLs should be injected through this class.
// CDN libs URLs are accessible throuh CDN object properties
// like Formio.cdn.ace === 'http://cdn.form.io/ace/1.4.12'.
// For latest version use empty string
class CDN {
  constructor(baseUrl) {
    this.baseUrl = baseUrl || 'https://cdn.form.io';
    this.overrides = {};
    this.libs = {
      'js': '',
      'ace': '1.4.12',
      'bootstrap': '4.6.2',
      'ckeditor': '19.0.0',
      'flatpickr': '4.6.8',
      'flatpickr-formio': '4.6.13-formio.3',
      'font-awesome': '4.7.0',
      'grid': 'latest',
      'moment-timezone': 'latest',
      'quill': '2.0.0-dev.3',
      'shortcut-buttons-flatpickr': '0.4.0',
      'uswds': '2.4.8',
      'core': ''
    };
    this.updateUrls();
  }

  getVersion(lib) {
    return this.libs[lib];
  }

  // Sets a specific library version
  setVersion(lib, version) {
    this.libs[lib] = version;
    this.updateUrls();
  }

  // Sets base CDN url for all libraries
  setBaseUrl(url) {
    this.baseUrl = url;
    this.updateUrls();
  }

  // Allows to override CDN url for a specific library
  setOverrideUrl(lib, url) {
    this.overrides[lib] = url;
    this.updateUrls();
  }

  // Removes override for a specific library
  removeOverride(lib) {
    delete this.overrides[lib];
    this.updateUrls();
  }

  // Removes all overrides
  removeOverrides() {
    this.overrides = {};
    this.updateUrls();
  }

  buildUrl(cdnUrl, lib, version) {
    let url;
    if (version === 'latest' || version === '') {
      url = `${cdnUrl}/${lib}`;
    }
    else {
      url = `${cdnUrl}/${lib}/${version}`;
    }
    return url;
  }

  updateUrls() {
    for (const lib in this.libs) {
      if (lib in this.overrides) {
        this[lib] = this.buildUrl(this.overrides[lib], lib, this.libs[lib]);
      }
      else {
        this[lib] = this.buildUrl(this.baseUrl, lib, this.libs[lib]);
      }
    }
  }
}

export default CDN;

import CDN from './CDN';
import assert from 'power-assert';

describe('Formio.js CDN class Tests', () => {
  let cdn;
  before(() => {
    cdn = new CDN('https://cdn.form.io');
  });

  it('Should give correct CDN URLs', () => {
    for (const lib in cdn.libs) {
      let expectedUrl = `${cdn.baseUrl}/${lib}/${cdn.libs[lib]}`;
      if (cdn.libs[lib] === '' || cdn.libs[lib] === 'latest') {
        expectedUrl = `${cdn.baseUrl}/${lib}`;
      }
      assert.equal(cdn[lib], expectedUrl);
    }
  });

  it('Should update lib versions', () => {
    cdn.setVersion('grid', '1.1.1');
    assert.equal(cdn.grid, 'https://cdn.form.io/grid/1.1.1');
  });

  it('Shoudl override CDN urls', () => {
    cdn.setOverrideUrl('grid', 'http://cdn.test-form.io');
    cdn.setVersion('grid', 'latest');
    assert.equal(cdn.grid, 'http://cdn.test-form.io/grid');

    cdn.setOverrideUrl('ace', 'http://cdn.test-form.io');
  });

  it('Should remove overrides', () => {
    cdn.removeOverrides();
    for (const lib in cdn.libs) {
      let expectedUrl = `${cdn.baseUrl}/${lib}/${cdn.libs[lib]}`;
      if (cdn.libs[lib] === '' || cdn.libs[lib] === 'latest') {
        expectedUrl = `${cdn.baseUrl}/${lib}`;
      }
      assert.equal(cdn[lib], expectedUrl);
    }
  });
});

import CDN from './CDN.js';
export class Formio {
    static baseUrl;
    static projectUrl;
    static language;
    static config = {};
    static cdn = null;
    static proxy = true;
    static version = 'FORMIO_VERSION';
    static async setBaseUrl(url) {
        Formio.baseUrl = url;
    }
    static async setProjectUrl(url) {
        Formio.projectUrl = url;
    }

    static debug(...args) {
        if (Formio.config.debug) {
            console.log(...args);
        }
    }

    static clearCache() {
        if (Formio.FormioClass) {
            Formio.FormioClass.clearCache();
        }
    }

    static global(prop) {
        const globalValue = window[prop];
        if (globalValue && globalValue.proxy) {
            return null;
        }
        Formio.debug(`Getting global ${prop}`, globalValue);
        return globalValue;
    }

    static createElement(type, attrs, children) {
        const element = document.createElement(type);
        Object.keys(attrs).forEach(key => {
            element.setAttribute(key, attrs[key]);
        });
        (children || []).forEach(child => {
            element.appendChild(Formio.createElement(child.tag, child.attrs, child.children));
        });
        return element;
    }

    static async addScript(wrapper, src, name) {
        if (!src) {
            return Promise.resolve();
        }
        if (typeof src !== 'string' && src.length) {
            return Promise.all(src.map(ref => Formio.addScript(wrapper, ref)));
        }
        if (name && Formio.global(name)) {
            Formio.debug(`${name} already loaded.`);
            return Promise.resolve(Formio.global(name));
        }
        Formio.debug('Adding Script', src);
        wrapper.appendChild(Formio.createElement('script', {
            src
        }));
        if (!name) {
            return Promise.resolve();
        }
        return new Promise((resolve) => {
            Formio.debug(`Waiting to load ${name}`);
            const wait = setInterval(() => {
                if (Formio.global(name)) {
                    clearInterval(wait);
                    Formio.debug(`${name} loaded.`);
                    resolve(Formio.global(name));
                }
            }, 100);
        });
    }

    static async addStyles(wrapper, href) {
        if (!href) {
            return;
        }
        if (typeof href !== 'string' && href.length) {
            href.forEach(ref => Formio.addStyles(wrapper, ref));
            return;
        }
        Formio.debug('Adding Styles', href);
        wrapper.appendChild(Formio.createElement('link', {
            rel: 'stylesheet',
            href
        }));
    }

    static async submitDone(instance, submission) {
        Formio.debug('Submision Complete', submission);
        const successMessage = (Formio.config.success || '').toString();
        if (successMessage && successMessage.toLowerCase() !== 'false' && instance.element) {
            instance.element.innerHTML = `<div class="alert-success" role="alert">${successMessage}</div>`;
        }
        let returnUrl = Formio.config.redirect;

        // Allow form based configuration for return url.
        if (
            !returnUrl &&
            (
                instance._form &&
                instance._form.settings &&
                (
                    instance._form.settings.returnUrl ||
                    instance._form.settings.redirect
                )
            )
        ) {
            Formio.debug('Return url found in form configuration');
            returnUrl = instance._form.settings.returnUrl || instance._form.settings.redirect;
        }

        if (returnUrl) {
            const formSrc = instance.formio ? instance.formio.formUrl : '';
            const hasQuery = !!returnUrl.match(/\?/);
            const isOrigin = returnUrl.indexOf(location.origin) === 0;
            returnUrl += hasQuery ? '&' : '?';
            returnUrl += `sub=${submission._id}`;
            if (!isOrigin && formSrc) {
                returnUrl += `&form=${encodeURIComponent(formSrc)}`;
            }
            Formio.debug('Return URL', returnUrl);
            window.location.href = returnUrl;
            if (isOrigin) {
                window.location.reload();
            }
        }
    }

    // Return the full script if the builder is being used.
    static formioScript(script, builder) {
        if (Formio.fullAdded || builder) {
            Formio.fullAdded = true;
            return script.replace('formio.form', 'formio.full');
        }
        return script;
    }

    // eslint-disable-next-line max-statements
    static async init(element, options = {}, builder = false) {
        Formio.cdn = new CDN(Formio.config.cdn);
        Formio.config.libs = Formio.config.libs || {
            uswds: {
                fa: true,
                js: `${Formio.cdn.uswds}/uswds.min.js`,
                css: `${Formio.cdn.uswds}/uswds.min.css`,
            },
            fontawesome: {
                css: `${Formio.cdn['font-awesome']}/css/font-awesome.min.css`
            },
            bootstrap: {
                css: `${Formio.cdn.bootstrap}/css/bootstrap.min.css`
            }
        };
        const id = Formio.config.id || `formio-${Math.random().toString(36).substring(7)}`;

        // Create a new wrapper and add the element inside of a new wrapper.
        let wrapper = Formio.createElement('div', {
            'id': `"${id}-wrapper"`
        });
        element.parentNode.insertBefore(wrapper, element);

        // If we include the libraries, then we will attempt to run this in shadow dom.
        if (Formio.config.includeLibs && (typeof wrapper.attachShadow === 'function') && !Formio.config.premium) {
            wrapper = wrapper.attachShadow({
                mode: 'open'
            });
            options.shadowRoot = wrapper;
        }

        element.parentNode.removeChild(element);
        wrapper.appendChild(element);

        // Load the renderer styles.
        await Formio.addStyles(wrapper, Formio.config.embedCSS || `${Formio.cdn.js}/formio.embed.css`);

        // Add a loader.
        wrapper.appendChild(Formio.createElement('div', {
            'class': 'formio-loader'
        }, [{
            tag: 'div',
            attrs: {
                class: 'loader-wrapper'
            },
            children: [{
                tag: 'div',
                attrs: {
                    class: 'loader text-center'
                }
            }]
        }]));

        Formio.FormioClass = await Formio.addScript(
            wrapper,
            Formio.formioScript(Formio.config.script || `${Formio.cdn.js}/formio.form.min.js`, builder),
            'Formio'
        );
        Formio.FormioClass.setBaseUrl(Formio.baseUrl || Formio.config.base);
        Formio.FormioClass.setProjectUrl(Formio.projectUrl || Formio.config.project);
        Formio.FormioClass.language = Formio.language;

        // Add premium modules
        if (Formio.global('premium')) {
            Formio.debug('Using premium module.');
            Formio.FormioClass.use(Formio.global('premium'));
        }

        if (Formio.global('vpat')) {
            Formio.debug('Using vpat module.');
            Formio.FormioClass.use(Formio.global('vpat'));
        }

        if (Formio.config.template) {
            if (Formio.config.includeLibs) {
                await Formio.addStyles(wrapper, Formio.config.libs[Formio.config.template].css);
                await Formio.addScript(wrapper, Formio.config.libs[Formio.config.template].js);
                if (Formio.config.libs[Formio.config.template].fa) {
                    await Formio.addStyles(wrapper, Formio.config.libs.fontawesome.css);
                }
            }

            if (Formio.cdn[Formio.config.template]) {
                const templateSrc = `${Formio.cdn[Formio.config.template]}/${Formio.config.template}.min`;
                await Formio.addStyles(wrapper, `${templateSrc}.css`);
                Formio.debug(`Using ${Formio.config.template}`);
                Formio.FormioClass.use(await Formio.addScript(wrapper, `${templateSrc}.js`, Formio.config.template));
            }
        }
        else if (Formio.global('uswds')) {
            Formio.debug('Using uswds module.');
            Formio.FormioClass.use(Formio.global('uswds'));
        }
        // Default bootstrap + fontawesome.
        else if (Formio.config.includeLibs) {
            await Formio.addStyles(wrapper, Formio.config.libs.fontawesome.css);
            await Formio.addStyles(wrapper, Formio.config.libs.bootstrap.css);
        }
        if (Formio.config.premium) {
            await Formio.addStyles(wrapper, Formio.config.premium.css);
            Formio.debug('Using premium');
            Formio.FormioClass.use(await Formio.addScript(wrapper, Formio.config.premium.js, 'premium'));
        }

        await Formio.addStyles(wrapper, Formio.formioScript(Formio.config.style || `${Formio.cdn.js}/formio.form.min.css`, builder));
        if (Formio.config.before) {
            await Formio.config.before(Formio.FormioClass, element, Formio.config);
        }
        Formio.FormioClass.license = true;
        return wrapper;
    }

    static async createForm(element, form, options) {
        const wrapper = await Formio.init(element, options);
        return Formio.FormioClass.createForm(element, form, {
            ...options,
            ...{ noLoader: true }
        }).then((instance) => {
            Formio.debug('Form created', instance);

            // Remove the loader.
            Formio.debug('Removing loader');
            wrapper.removeChild(wrapper.querySelector('.formio-loader'));

            // Set the default submission data.
            if (Formio.config.submission) {
                Formio.debug('Setting submission', Formio.config.submission);
                instance.submission = Formio.config.submission;
            }

            // Allow them to provide additional configs.
            Formio.debug('Triggering embed event');
            Formio.FormioClass.events.emit('formEmbedded', instance);

            // Trigger the after handler.
            if (Formio.config.after) {
                Formio.debug('Calling ready callback');
                Formio.config.after(instance, Formio.config);
            }

            return instance;
        });
    }

    static async builder(element, form, options) {
        const wrapper = await Formio.init(element, options, true);
        return Formio.FormioClass.builder(element, form, options).then((instance) => {
            Formio.debug('Builder created', instance);
            Formio.debug('Removing loader');
            wrapper.removeChild(wrapper.querySelector('.formio-loader'));
            Formio.debug('Triggering embed event');
            Formio.FormioClass.events.emit('builderEmbedded', instance);
            if (Formio.config.after) {
                Formio.debug('Calling ready callback');
                Formio.config.after(instance, Formio.config);
            }
            return instance;
        });
    }
}

export class Form {
    constructor(element, form, options) {
        this.form = form;
        this.element = element;
        this.options = options || {};
        this.init();
        this.instance = {
            proxy: true,
            ready: this.ready,
            destroy: () => {}
        };
    }

    init() {
        this.element.innerHTML = '';
        this.ready = this.create().then((instance) => {
            this.instance = instance;
            this.form = instance.form;
            return instance;
        });
    }

    create() {
        return Formio.createForm(this.element, this.form, this.options);
    }

    setDisplay(display) {
        if (this.instance.proxy) {
            return this.ready;
        }
        this.form.display = display;
        this.init();
        return this.ready;
    }
}

export class FormBuilder extends Form {
    create() {
        return Formio.builder(this.element, this.form, this.options);
    }
}

Formio.Form = Form;
Formio.FormBuilder = FormBuilder;

import { EventEmitter as EventEmitter3 } from 'eventemitter3';
import * as utils from './utils/utils';
export default class EventEmitter extends EventEmitter3 {
  constructor(conf = {}) {
    const { loadLimit = 1000, eventsSafeInterval = 300 } = conf;
    super();

    const overloadHandler = () => {
      console.warn(`There were more than ${loadLimit} events emitted in ${eventsSafeInterval} ms. It might be caused by events' infinite loop`, this.id);
    };

    const dispatch = utils.observeOverload(overloadHandler, {
      limit: loadLimit,
      delay: eventsSafeInterval
    });

    this.emit = (...args) => {
      super.emit(...args);
      super.emit('any', ...args);

      dispatch();
    };
  }

  onAny = (fn) => {
    this.on('any', fn);
  };

  offAny = (fn) => {
    this.off('any', fn);
  };
}

import Element from './Element';
import { Formio } from './Formio';
import Displays from './displays';
import templates from './templates';
import * as FormioUtils from './utils/utils';

export default class Form extends Element {
  /**
   * Creates an easy to use interface for embedding webforms, pdfs, and wizards into your application.
   *
   * @param {Object} element - The DOM element you wish to render this form within.
   * @param {Object | string} form - Either a Form JSON schema or the URL of a hosted form via. form.io.
   * @param {Object} options - The options to create a new form instance.
   * @param {boolean} options.readOnly - Set this form to readOnly
   * @param {boolean} options.noAlerts - Set to true to disable the alerts dialog.
   * @param {boolean} options.i18n - The translation file for this rendering. @see https://github.com/formio/formio.js/blob/master/i18n.js
   * @param {boolean} options.template - Provides a way to inject custom logic into the creation of every element rendered within the form.
   *
   * @example
   * import Form from '@formio/js/Form';
   * const form = new Form(document.getElementById('formio'), 'https://examples.form.io/example');
   * form.build();
   */
  constructor(...args) {
    let options = args[0] instanceof HTMLElement ? args[2] : args[1];
    if (Formio.options && Formio.options.form) {
      options = Object.assign(options, Formio.options.form);
    }

    super(options);

    if (this.options.useSessionToken) {
      Formio.useSessionToken(this.options);
    }

    this.ready = new Promise((resolve, reject) => {
      this.readyResolve = resolve;
      this.readyReject = reject;
    });

    this.instance = null;
    if (args[0] instanceof HTMLElement) {
      if (this.element) {
        delete this.element.component;
      }
      this.element = args[0];
      this.options = args[2] || {};
      this.options.events = this.events;
      this.setForm(args[1])
        .then(() => this.readyResolve(this.instance))
        .catch(this.readyReject);
    }
    else if (args[0]) {
      this.element = null;
      this.options = args[1] || {};
      this.options.events = this.events;
      this.setForm(args[0])
        .then(() => this.readyResolve(this.instance))
        .catch(this.readyReject);
    }
    else {
      this.element = null;
      this.options = {};
      this.options.events = this.events;
    }
    this.display = '';
  }

  createElement(tag, attrs, children) {
    const element = document.createElement(tag);
    for (const attr in attrs) {
      if (attrs.hasOwnProperty(attr)) {
        element.setAttribute(attr, attrs[attr]);
      }
    }
    (children || []).forEach(child => {
      element.appendChild(this.createElement(child.tag, child.attrs, child.children));
    });
    return element;
  }

  set loading(load) {
    if (!this.element || this.options.noLoader) {
      return;
    }
    if (load) {
      if (this.loader) {
        return;
      }
      this.loader = this.createElement('div', {
        'class': 'formio-loader'
      }, [{
        tag: 'div',
        attrs: {
          class: 'loader-wrapper'
        },
        children: [{
          tag: 'div',
          attrs: {
            class: 'loader text-center'
          }
        }]
      }]);
      this.element.appendChild(this.loader);
    }
    else if (this.loader) {
      this.element.removeChild(this.loader);
      this.loader = null;
    }
  }

  /**
   * Create a new form instance provided the display of the form.
   *
   * @param {string} display - The display of the form, either "wizard", "form", or "pdf"
   * @return {*}
   */
  create(display) {
    if (this.options && (this.options.flatten || this.options.renderMode === 'flat')) {
      display = 'form';
    }
    this.display = display;
    if (Displays.displays[display]) {
      return new Displays.displays[display](this.element, this.options);
    }
    else {
      // eslint-disable-next-line new-cap
      return new Displays.displays['webform'](this.element, this.options);
    }
  }

  /**
   * Sets the form. Either as JSON or a URL to a form JSON schema.
   *
   * @param {string|object} formParam - Either the form JSON or the URL of the form json.
   * @return {*}
   */
  set form(formParam) {
    this.setForm(formParam);
  }

  errorForm(err) {
    return {
      components: [
        {
          'label': 'HTML',
          'tag': 'div',
          'className': 'error error-message alert alert-danger ui red message',
          'attrs': [
            {
              'attr': 'role',
              'value': 'alert'
            }
          ],
          'key': 'errorMessage',
          'type': 'htmlelement',
          'input': false,
          'content': typeof err === 'string' ? err : err.message,
        }
      ]
    };
  }

  setForm(formParam) {
    let result;
    formParam = formParam || this.form;
    if (typeof formParam === 'string') {
      const formio = new Formio(formParam);
      let error;
      this.loading = true;
      result = this.getSubmission(formio, this.options)
        .catch((err) => {
          error = err;
        })
        .then((submission) => {
          return formio.loadForm()
          // If the form returned an error, show it instead of the form.
            .catch(err => {
              error = err;
            })
            .then((form) => {
              // If the submission returned an error, show it instead of the form.
              if (error) {
                form = this.errorForm(error);
              }
              this.loading = false;
              this.instance = this.instance || this.create(form.display);
              this.instance.url = formParam;
              this.instance.nosubmit = false;
              this._form = this.instance.form = form;
              if (submission) {
                this.instance.submission = submission;
              }
              if (error) {
                throw error;
              }
              return this.instance;
            });
        });
    }
    else {
      this.instance = this.instance || this.create(formParam.display);
      this._form = this.instance.form = formParam;
      result = this.instance.ready;
    }

    // A redraw has occurred so save off the new element in case of a setDisplay causing a rebuild.
    return result.then(() => {
      if (this.element) {
        delete this.element.component;
      }
      this.element = this.instance.element;
      return this.instance;
    });
  }

  getSubmission(formio, opts) {
    if (formio.submissionId) {
      return formio.loadSubmission(null, opts);
    }
    return Promise.resolve();
  }

  /**
   * Returns the loaded forms JSON.
   *
   * @return {object} - The loaded form's JSON
   */
  get form() {
    return this._form;
  }

  /**
   * Changes the display of the form.
   *
   * @param {string} display - The display to set this form. Either "wizard", "form", or "pdf"
   * @return {Promise<T>}
   */
  setDisplay(display) {
    if ((this.display === display) && this.instance) {
      return Promise.resolve(this.instance);
    }

    this.form.display = display;
    this.instance.destroy();
    this.instance = this.create(display);
    return this.setForm(this.form);
  }

  empty() {
    if (this.element) {
      while (this.element.firstChild) {
        this.element.removeChild(this.element.firstChild);
      }
    }
  }

  static embed(embed) {
    return new Promise((resolve) => {
      if (!embed || !embed.src) {
        resolve();
      }
      const id = this.id || `formio-${Math.random().toString(36).substring(7)}`;
      const className = embed.class || 'formio-form-wrapper';
      let code = embed.styles ? `<link rel="stylesheet" href="${embed.styles}">` : '';
      code += `<div id="${id}" class="${className}"></div>`;
      document.write(code);
      let attempts = 0;
      const wait = setInterval(() => {
        attempts++;
        const formElement = document.getElementById(id);
        if (formElement || attempts > 10) {
          resolve(new Form(formElement, embed.src).ready);
          clearInterval(wait);
        }
      }, 10);
    });
  }

  /**
   * Sanitize an html string.
   *
   * @param string
   * @returns {*}
   */
  sanitize(dirty, forceSanitize) {
    // If Sanitize is turned off
    if (this.options.sanitize === false && !forceSanitize) {
      return dirty;
    }
    return FormioUtils.sanitize(dirty, this.options);
  }

  setContent(element, content, forceSanitize) {
    if (element instanceof HTMLElement) {
      element.innerHTML = this.sanitize(content, forceSanitize);
      return true;
    }
    return false;
  }

  /**
   * Build a new form.
   *
   * @return {Promise<T>}
   */
  build() {
    if (!this.instance) {
      return Promise.reject('Form not ready. Use form.ready promise');
    }

    if (!this.element) {
      return Promise.reject('No DOM element for form.');
    }

    // Add temporary loader.
    const template = (this.options && this.options.template) ? this.options.template : 'bootstrap';
    const loader = templates[template].loader || templates.bootstrap.loader;
    this.setContent(this.element, loader.form);

    return this.render().then(html => {
      this.setContent(this.element, html);
      return this.attach(this.element).then(() => this.instance);
    })
      .then((param) => {
        this.emit('build', param);
        return param;
      });
  }

  render() {
    if (!this.instance) {
      return Promise.reject('Form not ready. Use form.ready promise');
    }
    return Promise.resolve(this.instance.render())
      .then((param) => {
        this.emit('render', param);
        return param;
      });
  }

  attach(element) {
    if (!this.instance) {
      return Promise.reject('Form not ready. Use form.ready promise');
    }
    if (this.element) {
      delete this.element.component;
    }
    this.element = element;
    return this.instance.attach(this.element)
      .then((param) => {
        this.emit('attach', param);
        return param;
      });
  }

  teardown() {
    super.teardown();
    delete this.instance;
    delete this.ready;
  }
}

