      const originalMakeRequest = Formio.makeRequest;

      Formio.makeRequest = function() {
        return new Promise(resolve => {
          const values = [
            { name : 'Alabama', abbreviation : 'AL' },
            { name : 'Alaska', abbreviation: { a:2, b: 'c' } },
            { name : 'American Samoa', abbreviation: true }
