    Conjunctions.conjunctions = { ...Conjunctions.conjunctions, ...conjunctions };
  }

  static getConjunction(name) {
    return Conjunctions.conjunctions[name];
  }

  static getConjunctions() {
    return Conjunctions.conjunctions;
  }
}

